(()=>{document.getElementById("copy-button").addEventListener("click",function(){const t=window.location.href,e=document.getElementById("copy-message");navigator.clipboard.writeText(t).then(()=>{e.style.display="block",e.textContent="복사되었습니다!",setTimeout(()=>{e.style.display="none"},3e3)}).catch(t=>{console.error("복사에 실패했습니다:",t),e.style.display="block",e.textContent="복사 실패",setTimeout(()=>{e.style.display="none"},3e3)})})})()